import React from 'react'
import {useHistory} from "react-router-dom"

const Header = () => {
    let history = useHistory()

    const handleAddJob = () => {
        history.push(`/add-job`)
    };

    return (
        <>
            <div className="row justify-content-end">
                <div className="col-3 mt-2 "><h4 className="text-center">Product Hiring</h4></div>
                <div className="col-5 mt-2">
                    <button onClick={(e) => handleAddJob(e)} className="btn btn-warning ">Post a job</button>
                </div>
            </div>
        </>
    )
}

export default Header;
