import React from 'react'
import { useHistory } from "react-router-dom"
const Header = () => {
    let history = useHistory()
    const handleAddJob = () => {
        history.push(`/add-job`)
    };
    return (
        <div class="row justify-content-center">
            <h1 className="font-weight-light display-1 text-center">Product Roles </h1> 
            <div ><button onClick={(e)=> handleAddJob(e)} className="btn btn-warning w-50">Post a job</button></div> 

        </div>
    )
}

export default Header;
