import React, {useContext, useEffect, useState} from 'react'
import { useHistory } from "react-router-dom"
import JobFinder from "../apis/JobFinder"
import { JobsContext } from '../context/JobContext'


const JobList = (props) => {

    const {robs, setJobs} = useContext(JobsContext)
    let history = useHistory()

    useEffect( () => {
        const fetchData = async () => {
            try {
                const response = await JobFinder.get("/");
                setJobs(response.data.data.robs)
             } catch(err){

             }
        }
        fetchData();

    },[])



    const handleApplyRedirect = (e, id, link) => {
        e.stopPropagation()
        window.location.assign(link);
    };

    const handleJobSelect = (id) => {
        history.push(`/robs/${id}`)
    };




    return (
        <div className="list-group">
            <h2>Today's Product Jobs</h2>
            <table className="table table-hover table-dark"> 
                <tbody>
                    {robs && robs.map(rob => {

                        return(
                            <div >
                                <div className="">
                                    <tr className="p-4"onClick={() => handleJobSelect(rob.id)} key={rob.id}>
                                        <td className="pl-5 pr-5">{rob.name}</td>
                                        <td className="pl-5 pr-5">{rob.location}</td>
                                        <td className="pl-5 pr-5">{rob.primary_tag}</td>
                                        <td className="pl-5 pr-5"><button onClick={(e) => handleApplyRedirect(e,rob.id,rob.link)} className="btn btn-danger">Apply</button></td>
                                    </tr>
                                </div>

                            </div>

                        );

                    })}
                </tbody>
            </table>
        </div>
    )
}

export default JobList
