import React, {useContext, useEffect} from 'react'
import { useHistory ,useParams} from "react-router-dom"
import JobFinder from "../apis/JobFinder"
import { JobsContext } from '../context/JobContext'
import MDEditor from '@uiw/react-md-editor';

const JobListDetail = (props) => {
    const {jobs, setJobs} = useContext(JobsContext)
    const {id} = useParams();
    let history = useHistory()
    useEffect( () => {
        const fetchData = async () => {
            try {
                const response = await JobFinder.get("/");
                setJobs(response.data.data.jobs)
             } catch(err){

             }
        }
        fetchData();
    },[])



    const handleApplyRedirect = (e, id, link) => {
        e.stopPropagation()
        window.location.assign(link);
    };

    const handleJobSelect = (id) => {
        history.push(``)
    };

    return (
        <div className="list-group">
            <h2>Today's Product Jobs</h2>
            <table className="table table-hover table-dark"> 
                <tbody>
                    {jobs && jobs.map(job => {
                        if(job.id === id){
                            return(
                                <React.Fragment>
                                <tr className=""onClick={() => handleJobSelect(job.id)} key={job.id}>

                                    <td className="pl-5 pr-5">{job.name}</td>
                                    <td className="pl-5 pr-5">{job.location}</td>
                                    <td className="pl-5 pr-5">{job.primary_tag}</td>
                                    <td className="pl-5 pr-5"><button onClick={(e) => handleApplyRedirect(e,job.id,job.link)} className="btn btn-danger">Apply</button></td>
                                </tr>

                                <tr>
                                    <div>
                                    <div className="p-3">
                                        <MDEditor.Markdown source={job.description}/>
                                    </div>
                                    <div className="p-4">
                                        <button onClick={(e) => handleApplyRedirect(e,job.id, job.link)} className="btn btn-danger">Apply for this position</button>
                                    </div>
                                    </div>

                                </tr> 
                                </React.Fragment>



                            );
                        }
                        else{
                            return(
                                <div className="">
                                    <div className="p-4s">
                                        <tr className="p-4"onClick={() => handleJobSelect(job.id)} key={job.id}>
                                            <td className="pl-5 pr-5">{job.name}</td>
                                            <td className="pl-5 pr-5">{job.location}</td>
                                            <td className="pl-5 pr-5">{job.primary_tag}</td>
                                            <td className="pl-5 pr-5"><button onClick={(e) => handleApplyRedirect(e,job.id)} className="btn btn-danger">Apply</button></td>
                                        </tr>
                                    </div>


                                </div>

                            );
                        }


                    })}
                </tbody>
            </table>
        </div>
    )
}

export default JobListDetail
